create table users
(
    id                     varchar                      not null
        constraint "PK_a3ffb1c0c8416b9fc6f907b7433"
            primary key,
    "userName"             varchar                      not null,
    email                  varchar                      not null,
    "hashedPassword"       varchar                      not null,
    "userType"             varchar                      not null,
    "avatarAssetId"        varchar                      not null,
    name                   varchar                      not null,
    theme                  varchar                      not null,
    language               varchar                      not null,
    "gameStreak"           integer                      not null,
    "createdAt"            timestamp                    not null,
    "updatedAt"            timestamp                    not null,
    description            varchar,
    roles                  text    default 'user'::text not null,
    isadmin                boolean default false        not null,
    "lastUserNameChangeAt" timestamp,
    "membershipType"       varchar                      not null,
    "membershipStartedAt"  timestamp                    not null,
    "membershipExpiresAt"  timestamp                    not null,
    status                 varchar                      not null
);



grant delete, insert, references, select, trigger, truncate, update on users to anon;

grant delete, insert, references, select, trigger, truncate, update on users to authenticated;

grant delete, insert, references, select, trigger, truncate, update on users to service_role;

